import React, { useState, useEffect, useRef } from "react";
import "../Dashboard.css";
import ChartSexo from "../../components/Charts/ChartSexo.jsx";
import ChartIdade from "../../components/Charts/ChartIdade.jsx";
import { fetchDataGet, fetchDataPost } from "../../controler/comunicacao.js";
import Modal from "../../components/Modal/Modal.jsx";
import ChartBar from "../../components/Charts/ChartBar.jsx";
import ChartLine from "../../components/Charts/ChartLine.jsx";
import ChartLineIncidencia from "../../components/Charts/ChartLineIncidencia.jsx";
import ChartLineTaxa from "../../components/Charts/ChartLineTaxa.jsx";
import InfoCard from "../../components/InfoCard/InfoCard.jsx";
import LoadingAnimation from "../../components/LoadingAnimation/LoadingAnimation.jsx";
import Dropdown from "../../components/Dropdown/Dropdown.jsx";
import DropdownItem from "../../components/Dropdown/DropdownItem.jsx";
import DropdownToolTip from "../../components/Dropdown/DropdownToolTip.jsx";

function DashBoard() {
  // Estado para armazenar o tipo de gráfico selecionado
  const [chartType, setChartType] = useState("bar");

  // Estado aplicar filtros
  const [aplicar, setAplicar] = useState(true);

  // Estados para armazenar as cidades e anos selecionados temporariamente
  const [tempSelectedCities, setTempSelectedCities] = useState([]);
  const [tempSelectedYears, setTempSelectedYears] = useState([]);

  // Estados para armazenar o id da tarefa
  const [taskId, setTaskId] = useState([]);

  // Referência para o iframe do mapa
  const iframeRef = useRef(null);

  // Dicionário para mapear os tipos de mapa
  const mapTypes = {
    incidencia: "Mapa de Incidência",
    calor: "Mapa de Calor",
    cluster: "Mapa de Cluster",
    moran: "Mapa de Moran",
  };

  // Estado para armazenar os filtros selecionados
  const [selectedMapa, setSelectedMapa] = useState("incidencia");
  const [selectedCities, setSelectedCities] = useState([]);
  const [selectedYears, setSelectedYears] = useState([]);
  const [selectedSexo, setSelectedSexo] = useState([]);
  const [selectedRaca, setSelectedRaca] = useState([]);
  const [selectedFaixaEtaria, setSelectedFaixaEtaria] = useState([]);
  const [selectedEscolaridade, setSelectedEscolaridade] = useState([]);
  const [selectedForma, setSelectedForma] = useState([]);

  // Estado para armazenar o flag, usada para mostrar ou esconder os filtros
  const [flag, setFlag] = useState(false);

  // Função para adicionar ou remover uma cidade da lista de selecionadas
  const toggleCitySelection = (city) => {
    if (city === "Todos") {
      setTempSelectedCities([]);
    } else if (tempSelectedCities.includes(city)) {
      setTempSelectedCities(tempSelectedCities.filter((item) => item !== city));
    } else {
      setTempSelectedCities([...tempSelectedCities, city]);
    }
  };

  // Função para adicionar ou remover um ano da lista de selecionados
  const handleYearSelect = (year) => {
    let currentYears = tempSelectedYears;

    if (currentYears.includes(year)) {
      if (currentYears.includes("Todos")) {
        currentYears = [];
      } else {
        currentYears = currentYears.filter((y) => y !== year);
      }
    } else if (year === "ultimos 10 anos") {
      currentYears = [];
    } else {
      if (currentYears.includes("Todos")) {
        currentYears = currentYears.filter((y) => y !== "Todos");
      }

      if (year === "Todos") {
        currentYears = ["Todos"];
      } else {
        currentYears = [...currentYears, year];
      }
    }

    setTempSelectedYears(currentYears);
  };

  // Função para adicionar ou remover uma raça da lista de selecionadas
  const handleRacaSelect = (raca) => {
    if (raca === "Todos") {
      setSelectedRaca([]);
    } else if (selectedRaca.includes(raca)) {
      setSelectedRaca(selectedRaca.filter((r) => r !== raca)); // Remove a raça selecionado
    } else {
      setSelectedRaca([...selectedRaca, raca]); // Adiciona a raça selecionado
    }
  };

  // Função para adicionar ou remover um sexo da lista de selecionados
  const handleSexoSelect = (sexo) => {
    if (sexo === "Todos") {
      setSelectedSexo([]);
    } else if (selectedSexo.length === 0) {
      setSelectedSexo([sexo]);
    } else if (selectedSexo.length === 1 && selectedSexo[0] === sexo) {
      setSelectedSexo([]); // Desmarca o sexo selecionado
    } else {
      setSelectedSexo([sexo]);
    }
  };

  // Função para adicionar ou remover uma faixa etária da lista de selecionadas
  const handleFaixaEtariaSelect = (idade) => {
    if (idade === "Todos") {
      setSelectedFaixaEtaria([]);
    } else if (selectedFaixaEtaria.includes(idade)) {
      setSelectedFaixaEtaria(selectedFaixaEtaria.filter((i) => i !== idade)); // Remove o ano selecionado
    } else {
      setSelectedFaixaEtaria([...selectedFaixaEtaria, idade]); // Adiciona o ano selecionado
    }
  };

  // Função para adicionar ou remover uma escolaridade da lista de selecionadas
  const handleEscolaridadeSelect = (escolaridade) => {
    if (escolaridade === "Todos") {
      setSelectedEscolaridade([]);
    } else if (selectedEscolaridade.includes(escolaridade)) {
      setSelectedEscolaridade(
        selectedEscolaridade.filter((e) => e !== escolaridade)
      ); // Remove a escolaridade selecionado
    } else {
      setSelectedEscolaridade([...selectedEscolaridade, escolaridade]); // Adiciona a escolaridade selecionado
    }
  };

  // Função para adicionar ou remover uma forma da lista de selecionadas
  const handleFormaSelect = (forma) => {
    if (forma === "Todos") {
      setSelectedForma([]);
    } else if (selectedForma.includes(forma)) {
      setSelectedForma(selectedForma.filter((f) => f !== forma)); // Remove a forma selecionado
    } else {
      setSelectedForma([...selectedForma, forma]); // Adiciona a forma selecionado
    }
  };

  // Função para aplicar os filtros selecionados
  const handleAplicar = () => {
    setSelectedCities(tempSelectedCities);
    setSelectedYears(tempSelectedYears);
    setAplicar(!aplicar)
  };

  // Estados para armazenar os dados
  const [loading, setLoading] = useState(true);
  const [loadingMapa, setLoadingMapa] = useState(true);
  const [mapa, setMapa] = useState([]);
  const [dadosGerais, setDadosGerais] = useState(undefined);
  const [dadosNumericos, setDadosNumericos] = useState({});
  const [dataGenero, setDataGenero] = useState([]);
  const [dataIdade, setDataIdade] = useState([]);
  const [dataRaca, setDataRaca] = useState([]);
  const [dataEscolaridade, setDataEscolaridade] = useState([]);
  const [dataIncidencia, setDataIncidencia] = useState([]);

  // useEffect para buscar os dados e aplicar os filtros selecionados
  useEffect(() => {
    setLoadingMapa(true);
    setLoading(true);

    // Parâmetros para a requisição
    const params = {
      SituacaoEncerramento: ["Abandono", "Abandono Primário"],
      AnoNotificacao:
        selectedYears.length > 0 ? selectedYears : ["ultimos 10 anos"],
      SexoPaciente: selectedSexo.length > 0 ? selectedSexo : ["Todos"],
      RacaPaciente: selectedRaca.length > 0 ? selectedRaca : ["Todos"],
      FaixaEtaria:
        selectedFaixaEtaria.length > 0 ? selectedFaixaEtaria : ["Todos"],
      Escolaridade:
        selectedEscolaridade.length > 0 ? selectedEscolaridade : ["Todos"],
      Forma: selectedForma.length > 0 ? selectedForma : ["Todos"],
      HIV: ["Todos"],
    };
    // Requisições para buscar os dados com base nos filtros selecionados
    Promise.all([
      fetchDataPost("/tuberculose/casos/", params),
      fetchDataPost(`/tuberculose/data/`, params),
      fetchDataPost("/tuberculose/sexo/", params),
      fetchDataPost("/tuberculose/idade/", params),
      fetchDataPost("/tuberculose/raca/", params),
      fetchDataPost("/tuberculose/escolaridade/", params),
      fetchDataPost("/tuberculose/incidencia/abandono/", params),
    ])
      // Atualiza os estados com os dados retornados
      .then(
        ([
          casosData,
          dataInit,
          dataSexo,
          dataIdade,
          dataRaca,
          dataEscolaridade,
          dataIncidencia,
        ]) => {
          setDadosGerais(casosData);
          setData(dataInit);
          setDataGenero(dataSexo);
          setDataIdade(dataIdade);
          setDataRaca(dataRaca);
          setDataEscolaridade(dataEscolaridade);
          setDataIncidencia(dataIncidencia);
        }
      ) 
      // Finaliza o loading
      .finally(() => {
        setLoading(false);
        setLoadingMapa(false);
      });
  }, [aplicar]);

  // Dados A serem inicializados
  const [years, setYears] = useState([]); // Inicialize como um array vazio
  const [data, setData] = useState([]);

  // useEffect para buscar o mapa
  useEffect(() => {
    const fetchMapData = async () => {
      setLoadingMapa(true);

      // Parâmetros para a requisição
      const params = {
        doenca: "TU",
        tipo: "ABANDONO",
        AnoNotificacao:
          selectedYears.length > 0 ? selectedYears : ["ultimos 10 anos"],
        tipo_mapa: "incidencia",
        HIV: "Negativo",
      };

      // Requisição para buscar o mapa
      try {
        const response = await fetchDataPost("/tuberculose/mapa/", params);

        const data = response; // Assumindo que response é um objeto
        if (data.status === "Encerrado") {
          setMapa(data.mapa);
          setLoadingMapa(false);
        } else if (data.status === "Não criado") {
          alert("O mapa está sendo Criado, clique em confirmar e aguarde");
          setTaskId(true);
        } else {
          setTaskId(true);
        }
      } catch (error) {
        // Tratamento de erro
        console.error("Error requesting map:", error);
        setLoadingMapa(false);
      }
    };
    // Verifica se a tarefa foi finalizada
    fetchMapData();
  }, [selectedYears, taskId]);

  // useEffect que verifica se o mapa foi finalizado
  useEffect(() => {
    if (taskId === true) {
      const interval = setInterval(async () => {
        setTaskId(false);
      }, 2000); // Polling interval de 2 segundos

      return () => clearInterval(interval);
    }
  }, [taskId]);

  // useEffect para buscar os anos disponíveis
  useEffect(() => {
    Promise.all([fetchDataGet("/tuberculose/coluna/AnoNotificacao")]).then(
      ([data]) => {
        if (data !== undefined) {
          setYears(data.sort().reverse());
        }
      }
    );
  }, []);

  // USE EFFECT PARA ATULIALIZAR AS CIDADES MARCADAS NO MAPA ENVIANDO UMA REQUISIÇÃO PARA
  // DENTRO DO ARQUIVO HTML DO MAPA
  useEffect(() => {
    const iframe = document.querySelector(".map-container");
    iframe.contentWindow.postMessage({ selectedCities }, "*");
  }, [selectedCities, selectedYears]);

  return (
    <section className="container-fluid dashboard px-0">
      <div className="row mb-3">
        <div className="col-12">
          <div className="titulo p-3 d-flex justify-content-between align-items-center">
            <div className="titulo-nome">
              Interrupção do Tratamento da Tuberculose
            </div>
            <div className="filter-button" onClick={() => setFlag(!flag)}>
              Filtros
            </div>
          </div>
        </div>
      </div>

      {/* FILTROS */}

      <div className={"on " + (flag ? "show mb-3" : "hide mb-0")}>
        <div className="row on-content">
          <div className="col-12">
            <div className="titulo p-3">
              <div className="filtros-wrapper">
                <div className="filtros mb-3">
                  {/* SELECT DE CIDADE */}

                  <div className="filtro pe-3">
                    <div className="card">
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="m-0">Escolha uma Cidade</h5>
                        <DropdownToolTip>
                          <p className="mb-2">
                            Selecione uma ou mais cidades para filtrar os dados apresentados.
                          </p>
                          <p className="mb-0">
                            Se nenhuma cidade for selecionada, os dados exibidos serão referentes a todo o estado de Mato Grosso.
                          </p>
                        </DropdownToolTip>
                      </div>
                      <div className="card-body">
                        <div className="form-check">
                          <input
                            type="checkbox"
                            id="city-todos"
                            value="Todos"
                            className="form-check-input"
                            checked={tempSelectedCities.length === 0}
                            onChange={() => toggleCitySelection("Todos")}
                          />
                          <label
                            htmlFor="city-todos"
                            className="form-check-label"
                          >
                            TODAS
                          </label>
                          {[
                            ...new Set(
                              data.map((item) => item.MunicipioResidencia)
                            ),
                          ]
                            .sort()
                            .map((city) => (
                              <div key={city} className="">
                                <input
                                  type="checkbox"
                                  id={`city-${city}`}
                                  value={city}
                                  className="form-check-input"
                                  checked={tempSelectedCities.includes(city)}
                                  onChange={() => toggleCitySelection(city)}
                                />
                                <label
                                  htmlFor={`city-${city}`}
                                  className="form-check-label"
                                >
                                  {city}
                                </label>
                              </div>
                            ))}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* SELECT DE ANO */}

                  <div className="filtro pe-3">
                    <div className="card">
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="m-0">Escolha um Ano</h5>
                        <DropdownToolTip>
                          <p className="mb-2">
                            Selecione um ou mais anos para filtrar os dados apresentados.
                          </p>
                          <p className="mb-0">
                            Caso nenhum ano seja selecionado, os dados exibidos corresponderão aos últimos 10 anos.
                          </p>
                        </DropdownToolTip>
                      </div>
                      <div className="card-body">
                        <div className="form-check">
                          <div className="">
                            <input
                              type="checkbox"
                              id="year-todos"
                              value="Todos"
                              className="form-check-input"
                              checked={tempSelectedYears.length === 0}
                              onChange={() => handleYearSelect("ultimos 10 anos")}
                            />
                            <label
                              htmlFor="year-todos"
                              className="form-check-label"
                            >
                              ULTIMOS 10 ANOS
                            </label>
                          </div>

                          <div className="">
                            <input
                              type="checkbox"
                              id="year-todos"
                              value="Todos"
                              className="form-check-input"
                              checked={tempSelectedYears.includes("Todos")}
                              onChange={() => handleYearSelect("Todos")}
                            />
                            <label
                              htmlFor="year-todos"
                              className="form-check-label"
                            >
                              TODOS
                            </label>
                          </div>
                          {years.map((item) => (
                            <div key={item} className="">
                              <input
                                type="checkbox"
                                id={`year-${item}`}
                                value={item}
                                className="form-check-input"
                                checked={tempSelectedYears.includes(item)}
                                onChange={() => handleYearSelect(item)}
                              />
                              <label
                                htmlFor={`year-${item}`}
                                className="form-check-label"
                              >
                                {item}
                              </label>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* SELECT DE GENERO */}

                  <div className="filtro pe-3">
                    <div className="card">
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="m-0">Escolha o Gênero</h5>
                        <DropdownToolTip>
                          <p className="mb-2">
                            Selecione um ou mais gêneros para filtrar os dados apresentados.
                          </p>
                          <p className="mb-0">
                            Caso nenhum gênero seja selecionado, os dados exibidos incluirão todos os gêneros.
                          </p>
                        </DropdownToolTip>
                      </div>
                      <div className="card-body">
                        <div className="form-check">
                          <input
                            type="checkbox"
                            id="sexo-ambos"
                            value="Todos"
                            className="form-check-input"
                            checked={selectedSexo.length === 0}
                            onChange={() => handleSexoSelect("Todos")}
                          />
                          <label
                            htmlFor="sexo-ambos"
                            className="form-check-label"
                          >
                            TODOS
                          </label>

                          <div className="">
                            <input
                              type="checkbox"
                              id="sexo-masculino"
                              value="M"
                              className="form-check-input"
                              checked={selectedSexo.includes("M")}
                              onChange={() => handleSexoSelect("M")}
                            />
                            <label
                              htmlFor="sexo-masculino"
                              className="form-check-label"
                            >
                              Masculino
                            </label>
                          </div>
                          <div className="">
                            <input
                              type="checkbox"
                              id="sexo-feminino"
                              value="F"
                              className="form-check-input"
                              checked={selectedSexo.includes("F")}
                              onChange={() => handleSexoSelect("F")}
                            />
                            <label
                              htmlFor="sexo-feminino"
                              className="form-check-label"
                            >
                              Feminino
                            </label>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* SELECT DE RAÇA */}

                  <div className="filtro pe-3">
                    <div className="card">
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="m-0">Escolha uma Raça</h5>
                        <DropdownToolTip>
                          <p className="mb-2">
                            Selecione uma ou mais raças para filtrar os dados apresentados.
                          </p>
                          <p className="mb-0">
                            Caso nenhuma raça seja selecionada, os dados exibidos incluirão todas as raças.
                          </p>
                        </DropdownToolTip>
                      </div>
                      <div className="card-body">
                        <div className="form-check">
                          <input
                            type="checkbox"
                            id="raca-todos"
                            value="Todos"
                            className="form-check-input"
                            checked={selectedRaca.length === 0}
                            onChange={() => handleRacaSelect("Todos")}
                          />
                          <label
                            htmlFor="raca-todos"
                            className="form-check-label"
                          >
                            TODAS
                          </label>

                          {[
                            "Parda",
                            "Branca",
                            "Preta",
                            "Indigena",
                            "Amarela",
                          ].map((raca) => (
                            <div key={raca} className="">
                              <input
                                type="checkbox"
                                id={`raca-${raca}`}
                                value={raca}
                                className="form-check-input"
                                checked={selectedRaca.includes(raca)}
                                onChange={() => handleRacaSelect(raca)}
                              />
                              <label
                                htmlFor={`raca-${raca}`}
                                className="form-check-label"
                              >
                                {raca}
                              </label>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* SELECT DE FAIXA ETARIA */}

                  <div className="filtro pe-3">
                    <div className="card">
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="m-0">Escolha uma Faixa Etária</h5>
                        <DropdownToolTip>
                          <p className="mb-2">
                            Selecione uma ou mais faixas etárias para filtrar os dados apresentados.
                          </p>
                          <p className="mb-0">
