import Chart from "react-apexcharts";
import "./chart.css";

export default function ChartColumn({
  data,
  selectedCities,
  column,
  title1,
  title2,
}) {

  const valores = [...new Set(data.map((item) => item[column]))].sort();
  let series = [];

  // delimitando numero de casas decimais de data.Incidencia

  // console.log("Valores:");
  //   console.log(valores);

  if (selectedCities.length === 0) {
    const totalCasos = valores.map((valor) => {
      const record = data.find(
        (item) =>
          item[column] === valor && item.MunicipioResidencia === "Mato Grosso"
      );
      return record ? record.Proporcao_Abandono : 0;
    }
    );

    // delimitando numero de casas decimais de totalCasos
    totalCasos.forEach((element, index) => {
      totalCasos[index] = element.toFixed(2);
    });


    // Criar uma única série de dados para representar o total de casos
    series.push({
      name: "Proporção de Interrupção de Tratamento",
      data: totalCasos,
    });

  } else {
    series = selectedCities.map((city) => {
      return {
        name: city,
        data: valores.map((valor) => {
          const record = data.find(
            (item) =>
              item[column] === valor && item.MunicipioResidencia === city
          );
          return record ? record.Proporcao_Abandono : 0;
        }),
      };
    });
  }

  const options = {
    dataLabels: {
      enabled: true,
    },
    chart: {
      type: "line",
    },
    stroke: {
      width: 3,
      curve: "straight",
    },
    xaxis: {
      categories: valores,
      title: {
        text: title1,
        style: {
          color: "var(--text-50)",
        },
      },
      labels: {
        style: {
          colors: "var(--text-50)",
        },
      },
    },
    yaxis: {
      title: {
        text: title2,
        style: {
          color: "var(--text-50)",
        },
      },
      labels: {
        style: {
          colors: "var(--text-50)",
        },
      },
    },
    tooltip: {
      theme: false,
      y: {
        formatter: function (val) {
          return val + "";
        },
      },
    },
    legend: {
      labels: {
        colors: "var(--text-50)",
      },
    },
  };

  return (
    <Chart
      options={options}
      series={series}
      type="line"
      height="100%"
      width="100%"
    />
  );
}
