import React, { useState, useEffect } from "react";
import Chart from "react-apexcharts";
import "./chart.css";

const ChartSexo = ({ data, selectedCities }) => {
  // Usando ApexCharts

  const [chartType, setChartType] = useState("pie");

  // console.log(data.filter((item) => selectedCities.includes(item.MunicipioResidencia) & item.SexoPaciente == "M").reduce((acc, curr) => acc + curr.NumeroCasos, 0))

  const values_m =
    selectedCities.length === 0
      ? data
          .filter((item) => item.SexoPaciente == "M")
          .reduce((acc, curr) => acc + curr.NumeroCasos, 0)
      : data
          .filter(
            (item) =>
              selectedCities.includes(item.MunicipioResidencia) &
              (item.SexoPaciente == "M")
          )
          .reduce((acc, curr) => acc + curr.NumeroCasos, 0);
  const values_f =
    selectedCities.length === 0
      ? data
          .filter((item) => item.SexoPaciente == "F")
          .reduce((acc, curr) => acc + curr.NumeroCasos, 0)
      : data
          .filter(
            (item) =>
              selectedCities.includes(item.MunicipioResidencia) &
              (item.SexoPaciente == "F")
          )
          .reduce((acc, curr) => acc + curr.NumeroCasos, 0);

  const values = [values_m, values_f];

  const labels = ["Masculino", "Feminino"];
  const series = values;

  const colors = ["#008FFB", "#FF4560"];

  const chartData = {
    series: series,
    options: {
      tooltip: {
        theme: false,
        y: {
          formatter: function (val) {
            return val + " Ocorrencias";
          },
        },
      },
      stroke: {
        width: 2,
        curve: "straight",
      },
      chart: {
        type: "pie",
      },
      labels: labels,
      colors: colors,
      legend: {
        show: true,
        position: "bottom",
        labels: {
          colors: "var(--text-color)",
        },
      },
    },
  };

  return (
    <Chart
      options={chartData.options}
      series={chartData.series}
      type={chartType}
      height="100%"
      width="100%"
    />
  );
};

export default ChartSexo;
