import React from "react";
import "./maps.css";
import { useState, useEffect, useRef } from "react";
import { fetchDataGet, fetchDataPost } from "../../controler/comunicacao";
import DropdownToolTip from "../../components/Dropdown/DropdownToolTip";
import Modal from "../../components/Modal/Modal";
import LoadingAnimation from "../../components/LoadingAnimation/LoadingAnimation";

export default function MapsNotif() {
  // flag para mostrar ou esconder os filtros
  const [flag, setFlag] = useState(false);

  // estados para armazenar os dados dos selects
  const [mapa, setMapa] = useState({});
  const [years, setYears] = useState([]);
  const [cities, setCities] = useState([]);

  // estados para armazenar os dados selecionados
  const [selectedMapa, setSelectedMapa] = useState("incidencia");
  const [selectedYears, setSelectedYears] = useState([]);
  const [selectedCities, setSelectedCities] = useState([]);
  const [selectedTipo, setSelectedTipo] = useState("Negativo");

  // estados para armazenar os dados dos selects temporários
  const [tempSelectedMapa, setTempSelectedMapa] = useState("incidencia");
  const [tempSelectedCities, setTempSelectedCities] = useState([]);
  const [tempSelectedYears, setTempSelectedYears] = useState([]);

  // estados para controlar o loading do mapa
  const [loadingMapa, setLoadingMapa] = useState(true);

  // Estados para armazenar o id da tarefa
  const [taskId, setTaskId] = useState([]);

  // Referência para o iframe do mapa
  const iframeRef = useRef(null);

  // Estado aplicar filtros
  const [aplicar, setAplicar] = useState(true);

  const valuesTypes = ["incidencia", "calor", "cluster", "moran"];

  // Dicionário para mapear os tipos de mapa
  const mapTypes = {
    incidencia: "Mapa de Incidência",
    calor: "Mapa de Calor",
    cluster: "Mapa de Cluster",
    moran: "Mapa de Moran",
  };

  // Função para adicionar ou remover uma cidade da lista de selecionadas
  const toggleCitySelection = (city) => {
    if (city === "Todos") {
      setTempSelectedCities([]);
    } else if (tempSelectedCities.includes(city)) {
      setTempSelectedCities(tempSelectedCities.filter((item) => item !== city));
    } else {
      setTempSelectedCities([...tempSelectedCities, city]);
    }
  };

  // Função para adicionar ou remover um ano da lista de selecionadas
  const handleYearSelect = (year) => {
    let currentYears = tempSelectedYears;

    if (currentYears.includes(year)) {
      if (currentYears.includes("Todos")) {
        currentYears = [];
      } else {
        currentYears = currentYears.filter((y) => y !== year);
      }
    } else if (year === "ultimos 10 anos") {
      currentYears = [];
    } else {
      if (currentYears.includes("Todos")) {
        currentYears = currentYears.filter((y) => y !== "Todos");
      }

      if (year === "Todos") {
        currentYears = ["Todos"];
      } else {
        currentYears = [...currentYears, year];
      }
    }

    setTempSelectedYears(currentYears);
  };

  // useEffect para buscar os anos e cidades disponíveis
  useEffect(() => {
    Promise.all([
      fetchDataGet("/tuberculose/coluna/AnoNotificacao"),
      fetchDataGet("/tuberculose/coluna/MunicipioResidencia"),
    ])
      .then(([data, municipios]) => {
        if (data !== undefined) {
          setYears(data.sort().reverse());
        }
        if (municipios !== undefined) {
          setCities(municipios.sort());
        }
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  // useEffect para buscar os dados do mapa
  useEffect(() => {
    const fetchMapData = async () => {
      setLoadingMapa(true);

      // Parâmetros para a requisição
      const params = {
        doenca: "TU",
        tipo: "ABANDONO",
        AnoNotificacao:
          selectedYears.length > 0 ? selectedYears : ["ultimos 10 anos"],
        tipo_mapa: selectedMapa,
        HIV: selectedTipo,
      };

      // Requisição para o backend
      try {
        const response = await fetchDataPost("/tuberculose/mapa/", params);

        const data = response; // Assumindo que response é um objeto
        if (data.status === "Encerrado") {
          setMapa(data.mapa);
          setLoadingMapa(false);
        } else if (data.status === "Não criado") {
          alert("O mapa está sendo Criado, clique em confirmar e aguarde");
          setTaskId(true);
        } else {
          setTaskId(true);
        }
      } catch (error) {
        // Tratar erros
        console.error("Error requesting map:", error);
        setLoadingMapa(false);
      }
    };
    // Verifica se finalizou a tarefa
    fetchMapData();
  }, [aplicar, taskId]);

  // useEffect para verificar se a tarefa foi finalizada
  useEffect(() => {
    if (taskId === true) {
      const interval = setInterval(async () => {
        setTaskId(false);
      }, 2000); // Polling interval de 2 segundos

      return () => clearInterval(interval);
    }
  }, [taskId]);

  // Função para aplicar os filtros
  const handleAplicar = () => {
    setSelectedCities(tempSelectedCities);
    setSelectedYears(tempSelectedYears);
    setSelectedMapa(tempSelectedMapa);
    setAplicar(!aplicar)
  };

  // useeffect para atualizar as cidades marcadas no mapa enviando uma requisição para
  // dentro do arquivo html do mapa
  useEffect(() => {
    const iframe = document.querySelector(".map-container");
    iframe.contentWindow.postMessage({ selectedCities }, "*");
  }, [selectedCities, selectedYears]);

  return (
    <section className="maps">

      {/* TITULO */}

      <div className="row mb-3">
        <div className="col-12">
          <div className="titulo p-3 d-flex justify-content-between align-items-center">
            <div className="titulo-nome">Mapas de Interrupções de Tratamento</div>
            <div className="filter-button" onClick={() => setFlag(!flag)}>
              Filtros
            </div>
          </div>
        </div>
      </div>

      {/* FILTROS */}

      <div className={"on " + (flag ? "show mb-3" : "hide mb-0")}>
        <div className="row on-content">
          <div className="col-12">
            <div className="titulo p-3">
              <div className="filtros-wrapper">
                <div className="filtros mb-3">
                  {/* SELECT DE ANO */}
                  <div className="filtro pe-3">
                    <div className="card">
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="m-0">Escolha um Ano</h5>
                        <DropdownToolTip>
                          <p className="mb-2">
                            Selecione um ou mais anos para filtrar os dados apresentados.
                          </p>
                          <p className="mb-0">
                            Caso nenhum ano seja selecionado, os dados exibidos corresponderão aos últimos 10 anos.
                          </p>
                        </DropdownToolTip>
                      </div>
                      <div className="card-body">
                        <div className="form-check">
                          <div className="">
                            <input
                              type="checkbox"
                              id="year-todos"
                              value="Todos"
                              className="form-check-input"
                              checked={tempSelectedYears.length === 0}
                              onChange={() =>
                                handleYearSelect("ultimos 10 anos")
                              }
                            />
                            <label
                              htmlFor="year-todos"
                              className="form-check-label"
                            >
                              ULTIMOS 10 ANOS
                            </label>
                          </div>

                          <div className="">
                            <input
                              type="checkbox"
                              id="year-todos"
                              value="Todos"
                              className="form-check-input"
                              checked={tempSelectedYears.includes("Todos")}
                              onChange={() => handleYearSelect("Todos")}
                            />
                            <label
                              htmlFor="year-todos"
                              className="form-check-label"
                            >
                              TODOS
                            </label>
                          </div>
                          {years.map((item) => (
                            <div key={item} className="">
                              <input
                                type="checkbox"
                                id={`year-${item}`}
                                value={item}
                                className="form-check-input"
                                checked={tempSelectedYears.includes(item)}
                                onChange={() => handleYearSelect(item)}
                              />
                              <label
                                htmlFor={`year-${item}`}
                                className="form-check-label"
                              >
                                {item}
                              </label>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* SELECT DE CIDADE */}

                  <div className="filtro pe-3">
                    <div className="card">
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="m-0">Escolha uma Cidade</h5>
                        <DropdownToolTip>
                          <p className="mb-2">
                            Selecione uma ou mais cidades para filtrar os dados apresentados.
                          </p>
                          <p className="mb-0">
                            Se nenhuma cidade for selecionada, os dados exibidos serão referentes a todo o estado de Mato Grosso.
                          </p>
                        </DropdownToolTip>
                      </div>
                      <div className="card-body">
                        <div className="form-check">
                          <input
                            type="checkbox"
                            id="city-todos"
                            value="Todos"
                            className="form-check-input"
                            checked={tempSelectedCities.length === 0}
                            onChange={() => toggleCitySelection("Todos")}
                          />
                          <label
                            htmlFor="city-todos"
                            className="form-check-label"
                          >
                            TODAS
                          </label>
                          {cities.map((city) => (
                            <div key={city} className="">
                              <input
                                type="checkbox"
                                id={`city-${city}`}
                                value={city}
                                className="form-check-input"
                                checked={tempSelectedCities.includes(city)}
                                onChange={() => toggleCitySelection(city)}
                              />
                              <label
                                htmlFor={`city-${city}`}
                                className="form-check-label"
                              >
                                {city}
                              </label>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* SELECT DE MAPA */}

                  <div className="filtro pe-3">
                    <div className="card">
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="m-0">Escolha um Mapa</h5>
                        <DropdownToolTip>
                          <p className="mb-2">
                            Utilize a seleção abaixo para escolher o tipo de mapa a ser visualizado.
                          </p>
                          <p className="mb-0">
                            Se nenhum tipo for selecionado, o mapa exibirá a incidência por padrão.
                          </p>
                        </DropdownToolTip>
                      </div>
                      <div className="card-body">
                        <div className="form-check">
                          {valuesTypes.map((key) => (
                            <div key={key} className="">
                              <input
                                type="checkbox"
                                id={`mapa-${key}`}
                                value={key}
                                className="form-check-input"
                                checked={tempSelectedMapa === key}
                                onChange={() => setTempSelectedMapa(key)}
                              />
                              <label
                                htmlFor={`mapa-${key}`}
                                className="form-check-label"
                              >
                                {mapTypes[key]}
                              </label>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* SELECT DE DOENÇA */}

                  <div className="filtro pe-3">
                    <div className="card">
                      <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="m-0">Tipo</h5>
                        <DropdownToolTip>
                          <p className="mb-2">
                            Selecione o tipo para filtrar os dados apresentados.
                          </p>
                          <p className="mb-0">
                            Caso nenhum tipo seja selecionado, os dados exibidos corresponderão a tuberculose.
                          </p>
                        </DropdownToolTip>
                      </div>
                      <div className="card-body">
                        <div className="form-check">

                          <div className="">
                            <input
                              type="checkbox"
                              id="tipo-TB"
                              value="TB"
                              className="form-check-input"
                              checked={selectedTipo === "Negativo"}
                              onChange={() => setSelectedTipo("Negativo")}
                            />
                            <label
                              htmlFor="tipo-TB"
                              className="form-check-label"
                            >
                              Tuberculose
                            </label>
                          </div>

                          <div className="">
                            <input
                              type="checkbox"
                              id="tipo-TB-HIV"
                              value="TB-HIV"
                              className="form-check-input"
                              checked={selectedTipo === "Positivo"}
                              onChange={() => setSelectedTipo("Positivo")}
                            />
                            <label
                              htmlFor="tipo-TB-HIV"
                              className="form-check-label"
                            >
                              Tuberculose + HIV
                            </label>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <button className="filter-button mt-3" onClick={()=>handleAplicar()}>Aplicar</button>
            </div>
          </div>
        </div>
      </div>

      {/* CARD MAPA */}
      <div className="col-12 mb-3">
        <div className="card">
          <div className="card-header d-flex justify-content-between align-items-center">
            <h5 className="m-0">{mapTypes[selectedMapa]}</h5>
            <div className="d-inline-flex align-items-center">
                <DropdownToolTip>
                  <p className="mb-2">
                    Utilize o mapa para visualizar a distribuição de Interrupções de Tratamento em diferentes regiões.
                  </p>
                  <p className="mb-2">
                    Passe o cursor sobre uma região para ver detalhes específicos sobre a distribuição de Interrupções de Tratamento nessa área.
                  </p>
                  <p className="mb-2">
                    <strong>Mapa de Calor:</strong> Visualiza a densidade de casos em diferentes regiões.
                  </p>
                  <p className="mb-2">
                    <strong>Mapa de Cluster:</strong> Identifica agrupamentos de alta e baixa incidência de casos.
                  </p>
                  <p className="mb-2">
                    <strong>Mapa de Moran:</strong> Mostra a autocorrelação espacial dos casos, indicando padrões espaciais.
                  </p>
                  <p className="mb-2">
                    <strong>Mapa de Incidência:</strong> Exibe a taxa de incidência de casos por região.
                  </p>
                  <p className="m-0">
                    O mapa abrange exclusivamente o intervalo de anos selecionado, do menor ao maior.
                  </p>
                </DropdownToolTip>

              <Modal title={mapTypes[selectedMapa]} id="ModalMap">
                <iframe
                  srcDoc={mapa}
                  title="Mapa Tuberculose"
                  width="100%"
                  height="100%"
                  className="map-container"
                />
              </Modal>
            </div>
          </div>
          <div className="card-body p-0 a">
            {loadingMapa === true ? (
              <LoadingAnimation />
            ) : (
              <iframe
                ref={iframeRef}
                srcDoc={mapa}
                title="Mapa Tuberculose"
                width="100%"
                height="100%"
                className="map-container"
                onLoad={() => {
                  if (iframeRef.current) {
                    iframeRef.current.contentWindow.postMessage(
                      { selectedCities },
                      "*"
                    );
                  }
                }}
              />
            )}
          </div>
        </div>
      </div>
    </section>
  );
}
